// equal height plugin - MD 2011
(function(){
	$.fn.makeEqualHeight = function(){
		var maxHeight = 0;
		this.each(function() {
			var height = parseInt($(this).outerHeight());
			if( height > maxHeight ) maxHeight = height;
		});
			
		return this.css('height', maxHeight + 'px');
	}
})(jQuery);

